import React, { useState } from 'react';
import {
  LiveKitRoom,
  VideoConference,
  RoomAudioRenderer,
} from '@livekit/components-react';

// Import the CSS styles directly
import '@livekit/components-styles/dist/index.css';
import './VideoRoom.css';

export const VideoRoom = ({ token, url, roomName, participantName, onLeaveRoom }) => {
  const [error, setError] = useState('');

  // Handle connection errors
  const handleError = (err) => {
    console.error('Error connecting to LiveKit:', err);
    setError(err.message || 'Failed to connect to room');
  };

  // Handle when the room is disconnected
  const handleDisconnected = () => {
    console.log('Disconnected from room');
    onLeaveRoom();
  };

  return (
    <div className="video-room">
      <div className="room-header">
        <h2>{roomName}</h2>
      </div>

      {error && <div className="error">{error}</div>}

      <LiveKitRoom
        token={token}
        serverUrl={url}
        options={{
          adaptiveStream: true,
          dynacast: true,
        }}
        video={true}
        audio={true}
        onDisconnected={handleDisconnected}
        onError={handleError}
        className="livekit-container"
      >
        <VideoConference />
        <RoomAudioRenderer />
      </LiveKitRoom>
    </div>
  );
};
