import React, { useEffect, useState } from 'react';
import {
  Room,
  VideoPresets,
  RoomEvent,
  RemoteParticipant,
  LocalParticipant,
  RemoteTrackPublication,
  LocalTrackPublication,
  Participant,
  Track,
  createLocalTracks
} from 'livekit-client';
import './VideoRoom.css';
import { Participant as ParticipantComponent } from './Participant';

export const VideoRoom = ({ token, url, roomName, participantName, onLeaveRoom }) => {
  const [room, setRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [localParticipant, setLocalParticipant] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isScreenSharing, setIsScreenSharing] = useState(false);
  const [activeScreenShare, setActiveScreenShare] = useState(null);
  const [error, setError] = useState('');

  // Connect to the room when component mounts
  useEffect(() => {
    let roomInstance = null;
    let isMounted = true;

    const connect = async () => {
      try {
        console.log('Attempting to connect to LiveKit room:', roomName);
        console.log('Using URL:', url);
        
        // Create a new room instance
        roomInstance = new Room({
          adaptiveStream: true,
          dynacast: true,
          stopMicTrackOnMute: false,
        });
        
        setRoom(roomInstance);

        // Set up event listeners
        setupRoomEventListeners(roomInstance);
        
        // Connect to the LiveKit room
        console.log('Connecting to LiveKit with token...');
        await roomInstance.connect(url, token);
        
        if (!isMounted) {
          console.log('Component unmounted during connection, disconnecting');
          roomInstance.disconnect();
          return;
        }
        
        console.log('Successfully connected to room:', roomName);
        setIsConnected(true);
        
        // Enable camera and microphone
        console.log('Creating local tracks...');
        const tracks = await createLocalTracks({
          audio: true,
          video: true,
          videoPreset: VideoPresets.h720
        });
        
        if (!isMounted) {
          console.log('Component unmounted after creating tracks, disconnecting');
          roomInstance.disconnect();
          return;
        }
        
        console.log('Publishing tracks to room...');
        await roomInstance.localParticipant.publishTracks(tracks);
        setLocalParticipant(roomInstance.localParticipant);
        
        // Update participants list
        updateParticipants(roomInstance);
      } catch (err) {
        console.error('Error connecting to room:', err);
        console.error('Error details:', {
          name: err.name,
          message: err.message,
          stack: err.stack,
          roomName,
          url
        });
        
        if (isMounted) {
          setError(err.message || 'Failed to connect to room');
        }
        
        // Clean up on error
        if (roomInstance) {
          try {
            roomInstance.disconnect();
          } catch (disconnectErr) {
            console.error('Error during disconnect after connection failure:', disconnectErr);
          }
        }
      }
    };

    connect();

    // Clean up when component unmounts
    return () => {
      console.log('Component unmounting, cleaning up room connection');
      isMounted = false;
      
      if (roomInstance) {
        try {
          // First remove all listeners to prevent callback errors during dismount
          roomInstance.removeAllListeners();
          
          // Then disconnect from the room
          console.log('Disconnecting from room');
          roomInstance.disconnect();
        } catch (err) {
          console.error('Error during cleanup:', err);
        }
      }
    };
  }, [token, url, roomName, participantName]);

  // Setup room event listeners
  const setupRoomEventListeners = (roomInstance) => {
    roomInstance.on(RoomEvent.ParticipantConnected, () => {
      updateParticipants(roomInstance);
    });
    
    roomInstance.on(RoomEvent.ParticipantDisconnected, () => {
      updateParticipants(roomInstance);
    });
    
    roomInstance.on(RoomEvent.TrackSubscribed, (track, publication, participant) => {
      // Handle screen share
      if (track.kind === Track.Kind.Video && track.source === Track.Source.ScreenShare) {
        setActiveScreenShare({
          participant,
          track
        });
      }
      updateParticipants(roomInstance);
    });
    
    roomInstance.on(RoomEvent.TrackUnsubscribed, (track, publication, participant) => {
      // Handle screen share ended
      if (track.kind === Track.Kind.Video && track.source === Track.Source.ScreenShare) {
        setActiveScreenShare(null);
      }
      updateParticipants(roomInstance);
    });
    
    roomInstance.on(RoomEvent.Disconnected, () => {
      console.log('Room disconnected event received');
      setIsConnected(false);
      onLeaveRoom();
    });
    
    roomInstance.on(RoomEvent.ConnectionStateChanged, (state) => {
      console.log('Connection state changed:', state);
    });
  };

  // Update the participants state
  const updateParticipants = (roomInstance) => {
    if (!roomInstance) return;
    
    const remotes = Array.from(roomInstance.remoteParticipants.values());
    setParticipants(remotes);
  };

  // Toggle video
  const toggleVideo = async () => {
    if (!room || !localParticipant) return;
    
    try {
      // Get video track
      const videoTracks = localParticipant.getTracks(Track.Source.Camera);
      
      if (videoTracks.length === 0) {
        // Publish new video track if not available
        if (isVideoEnabled) {
          const tracks = await createLocalTracks({
            video: true,
            videoPreset: VideoPresets.h720
          });
          await localParticipant.publishTracks(tracks);
        }
      } else {
        // Toggle existing video track
        for (const publication of videoTracks) {
          if (isVideoEnabled) {
            await publication.mute();
          } else {
            await publication.unmute();
          }
        }
      }
      
      setIsVideoEnabled(!isVideoEnabled);
    } catch (err) {
      console.error('Error toggling video:', err);
      setError('Failed to toggle video');
    }
  };

  // Toggle audio
  const toggleAudio = async () => {
    if (!room || !localParticipant) return;
    
    try {
      // Get audio track
      const audioTracks = localParticipant.getTracks(Track.Source.Microphone);
      
      if (audioTracks.length === 0) {
        // Publish new audio track if not available
        if (isAudioEnabled) {
          const tracks = await createLocalTracks({
            audio: true
          });
          await localParticipant.publishTracks(tracks);
        }
      } else {
        // Toggle existing audio track
        for (const publication of audioTracks) {
          if (isAudioEnabled) {
            await publication.mute();
          } else {
            await publication.unmute();
          }
        }
      }
      
      setIsAudioEnabled(!isAudioEnabled);
    } catch (err) {
      console.error('Error toggling audio:', err);
      setError('Failed to toggle audio');
    }
  };

  // Toggle screen sharing
  const toggleScreenShare = async () => {
    if (!room || !localParticipant) return;
    
    try {
      if (!isScreenSharing) {
        // Start screen sharing
        const screenTrack = await createLocalTracks({
          video: {
            source: Track.Source.ScreenShare
          }
        });
        
        // Publish screen track
        await localParticipant.publishTracks(screenTrack);
        setIsScreenSharing(true);
      } else {
        // Stop screen sharing
        const screenTracks = localParticipant.getTracks(Track.Source.ScreenShare);
        
        for (const publication of screenTracks) {
          await localParticipant.unpublishTrack(publication.track);
        }
        
        setIsScreenSharing(false);
      }
    } catch (err) {
      console.error('Error toggling screen share:', err);
      // User cancelled the screen share selection
      if (err.name === 'NotAllowedError') {
        return;
      }
      setError('Failed to toggle screen share');
    }
  };

  // Leave room
  const leaveRoom = () => {
    if (room) {
      room.disconnect();
      setIsConnected(false);
      onLeaveRoom();
    }
  };

  return (
    <div className="video-room">
      <div className="room-header">
        <h2>{roomName}</h2>
        <div className="controls">
          <button
            className={`control-button ${!isVideoEnabled ? 'disabled' : ''}`}
            onClick={toggleVideo}
          >
            {isVideoEnabled ? 'Turn Off Camera' : 'Turn On Camera'}
          </button>
          <button
            className={`control-button ${!isAudioEnabled ? 'disabled' : ''}`}
            onClick={toggleAudio}
          >
            {isAudioEnabled ? 'Mute Mic' : 'Unmute Mic'}
          </button>
          <button
            className={`control-button ${isScreenSharing ? 'active' : ''}`}
            onClick={toggleScreenShare}
          >
            {isScreenSharing ? 'Stop Sharing' : 'Share Screen'}
          </button>
          <button className="control-button leave" onClick={leaveRoom}>
            Leave Room
          </button>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className={`participants-container ${activeScreenShare ? 'with-screen-share' : ''}`}>
        {activeScreenShare && (
          <div className="screen-share">
            <div className="screen-share-video" id="screen-share-video"></div>
          </div>
        )}

        <div className="participants">
          {localParticipant && (
            <ParticipantComponent
              key="local"
              participant={localParticipant}
              isLocal={true}
            />
          )}
          
          {participants.map(participant => (
            <ParticipantComponent
              key={participant.sid}
              participant={participant}
              isLocal={false}
            />
          ))}
        </div>
      </div>
    </div>
  );
};
